var currentToggle = null;

$(document).ready(function() {

        /*julio*/

    $('.nav-toggle').click(function(){

        var toggle_switch = $(this);

        if (toggle_switch.hasClass('pending')) {
            return;
        }

        var collapse_content_selector = $(this).attr('href');
        var $collapse_content_selector = $(collapse_content_selector);
        $('.ocultar').slideUp();
        $('.nav-toggle ')
            .removeClass('pending')
            .html("");
        if (currentToggle == collapse_content_selector) {
            currentToggle = null;
            return;
        }
        currentToggle = collapse_content_selector;
        toggle_switch.addClass('pending');

        $collapse_content_selector.slideDown(function(){
            toggle_switch.removeClass('pending');
        });

        toggle_switch.html("");
    });
});

